import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import Clock from './components/Clock/Clock';

function App() {
  const [showSettings, setShowSettings] = useState(false);
  const settingsTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  const handleAppClick = () => {
    setShowSettings(true);
    if (settingsTimeoutRef.current) {
      clearTimeout(settingsTimeoutRef.current);
    }
    settingsTimeoutRef.current = setTimeout(() => {
      setShowSettings(false);
    }, 5000); // Hide settings icon after 5 seconds of inactivity
  };

  useEffect(() => {
    // Clear timeout on component unmount
    return () => {
      if (settingsTimeoutRef.current) {
        clearTimeout(settingsTimeoutRef.current);
      }
    };
  }, []);

  return (
    <div className="App" onClick={handleAppClick}>
      <Clock />
      {showSettings && (
        <button className="settings-button" onClick={(e) => { 
          e.stopPropagation(); // Prevent App click handler when clicking button
          // TODO: Implement settings menu toggle logic here
          alert('Settings menu to be implemented!'); 
        }}>
          ⚙️
        </button>
      )}
    </div>
  );
}

export default App;
