import React from 'react';
import './SettingsMenu.css';

interface SettingsMenuProps {
  isOpen: boolean;
  onClose: () => void;
  clockTextColor: string;
  onClockTextColorChange: (color: string) => void;
  appBackgroundColor: string;
  onAppBackgroundColorChange: (color: string) => void;
}

const SettingsMenu: React.FC<SettingsMenuProps> = ({
  isOpen,
  onClose,
  clockTextColor,
  onClockTextColorChange,
  appBackgroundColor,
  onAppBackgroundColorChange,
}) => {
  if (!isOpen) {
    return null;
  }

  return (
    <div className="settings-menu-overlay" onClick={onClose}>
      <div className="settings-menu" onClick={(e) => e.stopPropagation()}>
        <h2>Settings</h2>

        <div className="setting-item">
          <label htmlFor="clockTextColor">Clock Text Color:</label>
          <input 
            type="color" 
            id="clockTextColor" 
            value={clockTextColor} 
            onChange={(e) => onClockTextColorChange(e.target.value)} 
          />
        </div>

        <div className="setting-item">
          <label htmlFor="appBackgroundColor">Background Color:</label>
          <input 
            type="color" 
            id="appBackgroundColor" 
            value={appBackgroundColor} 
            onChange={(e) => onAppBackgroundColorChange(e.target.value)} 
          />
        </div>

        <button onClick={onClose} className="close-button">Close</button>
      </div>
    </div>
  );
};

export default SettingsMenu;
