import React, { useState, useEffect } from 'react';
import './Clock.css';

interface ClockProps {
  textColor?: string;
  fontFamily?: string;
  textSize?: string;
  isKlockMode?: boolean;
}

const Clock: React.FC<ClockProps> = ({ textColor, fontFamily, textSize, isKlockMode }) => {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const timerId = setInterval(() => {
      setTime(new Date());
    }, 1000);

    return () => {
      clearInterval(timerId);
    };
  }, []);

  const formatTime = (date: Date): string => {
    const hours = date.getHours().toString().padStart(2, '0');
    const minutes = date.getMinutes().toString().padStart(2, '0');
    if (isKlockMode) {
      return `${hours}:${minutes}`;
    }
    const seconds = date.getSeconds().toString().padStart(2, '0');
    return `${hours}:${minutes}:${seconds}`;
  };

  return (
    <div className="clock-container">
      <div className="clock-display" style={{ color: textColor, fontFamily: fontFamily, fontSize: textSize }}>
        {formatTime(time)}
      </div>
      {isKlockMode && (
        <div className="klock-progress-bar-container">
          <div 
            className="klock-progress-bar"
            style={{
              width: `${(time.getSeconds() / 60) * 100}%`,
              backgroundColor: textColor
            }}
          ></div>
        </div>
      )}
    </div>
  );
};

export default Clock;
